mapper.select:
select count(*) from t_thales_scheduler_job_instance instance left join t_thales_scheduler_job job on instance.job_id = job.id where 1=1

     and instance.task_state = #{taskState}     


     and job.job_name like concat(concat('%',#{jobName}),'%')


     and job.creator_id = #{userName}

select instance.id,job.job_name,job.job_type,job.job_ReleaseState,instance.task_state,instance.log_url,instance.log_path,instance.worker,instance.creator_name, instance.creator_email,instance.retry_times,instance.pid,instance.applicationId,instance.elapse_time, instance.schedule_time,instance.start_time,instance.end_time,instance.create_time,instance.update_time from t_thales_scheduler_job_instance instance left join t_thales_scheduler_job job on instance.job_id = job.id where 1=1
and instance.task_state = #{taskState}
and job.creator_id = #{userName}
and job.job_name like concat(concat('%',#{jobName}),'%')
ORDER BY instance.create_time DESC

mapper.update:
update t_thales_scheduler_job_instance
set task_state = #{status,jdbcType=TINYINT},
update_time = #{date,jdbcType=TIMESTAMP} 
where 1=1 

   and id in

      #{id}
